    public static int partOneCalc(String input) {
        int result = 0;
        for (char parenthesis : input.toCharArray()) {
            if (parenthesis == '(')
                result++;
            else
                result--;
        }
        return result;
    }

    public static int partTwoCalc(String input) {
        int result = 1;
        for (int i = 0; i < input.length(); i++) {
            if (input.charAt(i) == '(')
                result++;
            else
                result--;
            if (result == -1) {
                result = i;
                break;
            }
        }

        return result;
    }

